sourcesContent:
import mongoose from 'mongoose';
const { Schema } = mongoose;

const userSchema = new Schema({
   username:{
    type: String,
    required: true,
    unique: true,
 },
 email:{
    type: String,
    required: true,
    unique: true,
 },
 password:{
    type: String,
    required: true,
    
 },
 img:{
    type: String,
    required: false,
    
 },
 phoneNumber:{
    type: String,
    required: false,  
 }, 

  isAdmin:{
    type: Boolean,
    default: false,
    
 },
},
{
    timestamps: true,
});

const Users= mongoose.model("Users", userSchema);
module.exports = Users;

